def get_latest_publication_details(query):

    search_query = scholarly.search_pubs(query)

    try:

        publication = next(search_query)

        # Extract publication details
        title = publication.get('bib', {}).get('title', 'Unknown Title')
        authors = ", ".join(publication.get('bib', {}).get('author', [])) or "Unknown Authors"
        pub_date = publication.get('bib', {}).get('pub_year', 'Unknown Year')
        pub_institution = publication.get('bib', {}).get('venue', 'Unknown Institution')
        link = publication.get('eprint_url', 'No Link Available')

        # Return the formatted response
        formatted_output = (
            f"The latest publication on {query}, titled \"{title}\", "
            f"was published in {pub_institution} on {pub_date}. "
            f"The authors are {authors}. You can read it [here]({link})."
        )

        return formatted_output

    except StopIteration:
        return "No publications found ."

